public static int binarySearch(double[] arr, double find) {
    int lo = 0;
    int hi = arr.length - 1;

    while (lo + 1 &lt; hi) {
        int mid = (lo + hi) / 2;
        int res = Double.compare(find, arr[mid]);

        if (res == 0)
