		if (numberOfEntries == 0) {
			result = true;
		} else {
			result = false;
		} 
		return result;
	}
	
	public T[] toArray() {
		@SuppressWarnings(&quot;unchecked&quot;)
		T[] result = (T[])new Object[numberOfEntries];
		
		int index = 0;
		Node currentNode = firstNode;
		while ((index &lt; numberOfEntries) &amp;&amp; (currentNode != null)) {
			result[index] = currentNode.getData();
			currentNode = currentNode.getNextNode();
			index++;
		}
		return result;
	} // end to array
	
	
	
	public T remove(int givenPosition) {
		T result = null;
		if((givenPosition &gt;= 1) &amp;&amp; (givenPosition &lt;= numberOfEntries)) {
			if (givenPosition == 1) {
				result = firstNode.getData();
				firstNode = firstNode.getNextNode();
			} else {
				Node nodeBefore = getNodeAt(givenPosition - 1);
				Node nodeToRemove = nodeBefore.getNextNode();
				result = nodeToRemove.getData();
				Node nodeAfter = nodeToRemove.getNextNode();
				nodeBefore.setNextNode(nodeAfter);
			}
			numberOfEntries--;
			return result;
			
		} else {
			throw new IndexOutOfBoundsException(&quot;illegal position given to remove &quot;);
		}
	} // end remove
	
	public T replace(int givenPosition, T newEntry) {
		if((givenPosition &gt;= 1) &amp;&amp; (givenPosition &lt;= numberOfEntries)) {
			Node desiredNode = getNodeAt(givenPosition);
			T originalEntry = desiredNode.getData();
			return originalEntry;
		}
		else
			throw new IndexOutOfBoundsException(&quot;Illegal Position given for replace method &quot;);
	} // end replace
	
	public T getEntry(int givenPosition) {
		if ((givenPosition &gt;= 1) &amp;&amp; (givenPosition &lt;= numberOfEntries)) {
			return getNodeAt(givenPosition).getData;
		} 
		else 
			throw new IndexOutOfBoundsException(&quot;Illegal Position given for getentry method &quot;);
	} // end get entry
	
	public boolean contains(T anEntry) {
		boolean found = false;
		Node currentNode = firstNode;
		
		while(!found &amp;&amp; (currentNode != null)) {
			if(anEntry.equals(currentNode.getData())) {
				found = true;
			} else {
				currentNode = currentNode.getNextNode;
			}
			
		}
		return found;
	}
	
	
	
	public Iterator&lt;T&gt; iterator() {
		return new IteratorForLinkedList();
	}
	
	public Iterator getIterator() {
		return iterator();
	}
	
	private class IteratorForLinkedList implements Iterator&lt;T&gt; {
		private Node nextNode;
		
		private IteratorForLinkedList() {
			nextNode = firstNode;
		}
		
		public T next() {
			T result;
			if (hasNext()) {
