                Assert.assertTrue(((List&lt;Train&gt;)pag.get(&quot;trains&quot;)).size() == 20);
            }
        
            @Test
            public void switchHelper() {
                Assert.assertTrue(controllerService.stationSwitchHelper(&quot;BLUE&quot;).equals(URLs.REDIRECT_DASHSTATION + &quot;/2&quot;));
            }
        }

