public class LinkedListDeque {



    public node sentinel;
    public int size;

    public class node {
        public node prev;
        public int item;
        public node next;

        public node(node p, int i, node n) {
            prev = p;
            item = i;
            next = n;
        }
    }

    // Creates an empty list
    public LinkedListDeque() {
        sentinel = new node(null, 63, null);
        sentinel.prev = sentinel;
        sentinel.next = sentinel;
        size = 0;
    }



    // Adds an item of type T to the front of the deque.
    public void addFirst(int x) {
        sentinel.next = new node(sentinel, x, sentinel.next);
        sentinel.next.next.prev = sentinel.next;
        size += 1;
    }



    // Adds an item of type T to the back of the deque.
    public void addLast(int x) {
        sentinel.prev = new node(sentinel.prev, x, sentinel);
        sentinel.prev.prev.next = sentinel.prev;
        size += 1;
    }


    // Returns true if deque is empty, false otherwise.
    public boolean isEmpty() {
        if (size == 0) {
            return true;
        }
        return false;
    }


    // Returns the number of items in the deque.
    public int size() {
        return size;
    }


    // Prints the items in the deque from first to last, separated by a space. Once all the items have been printed, print out a new line.
    public void printDeque() {
        node p = sentinel.next;
        for (int i=0; i&lt;size; i++) {
            System.out.print(p.item + &quot; &quot;);
            p = p.next;
        }
        System.out.println();
    }


    // Removes and returns the item at the front of the deque. If no such item exists, returns null.
    public int removeFirst() {
        if (size==0) {
            return 0;
        }
        int x = sentinel.next.item;
        sentinel.next = sentinel.next.next;
        sentinel.next.prev = sentinel;
        size -= 1;
        return x;
    }


    // Removes and returns the item at the back of the deque. If no such item exists, returns null.
    public int removeLast() {
        if (size==0) {
            return 0;
        }
        int x = sentinel.prev.item;
        sentinel.prev = sentinel.prev.prev;
        sentinel.prev.next = sentinel;
        size -= 1;
        return x;

    }

    // Gets the item at the given index, where 0 is the front, 1 is the next item, and so forth. If no such item e
    public int get(int index) {
        if (index&gt;size-1) {
            return 0;
        }
        node p = sentinel.next;
        for (int i=0; i&lt;index; i++) {
            p = p.next;

        }
        return p.item;
    }


    //Same as get, but uses recursion.
    public int getRecursive(int index) {
        if (index&gt;size-1) {
            return 0;
        }
        node p = sentinel;
        return getRecursiveHelper(p, index);
    }

    public int getRecursiveHelper(node p, int i) {
        if (i==0) {
            return p.next.item;
        } else {
            p = p.next;
            i -= 1;
        }
        return getRecursiveHelper(p, i);

    }









    public static void main(String[] args) {
        LinkedListDeque L = new LinkedListDeque();
        L.addFirst(2);
        L.addFirst(1);
        L.addFirst(0);
        L.addLast(3);
        L.addLast(4);
        L.addLast(5);
        System.out.println(L.get(7));




    }

}

