    public class RomanToDecimal {
      private HashMap&lt;Character, Integer&gt; map;
      
      public RomanToDecimal() {
        map = new HashMap&lt;&gt;();
        map.put(&#39;I&#39;, 1);
        map.put(&#39;V&#39;, 5);
