	}
	
	public E set(int index, E element) {
		if(isEmpty()) return null;
		Node&lt;E&gt; current = header;
		E returnVal = null;
		int count = 0;
		while(current != null) {
			if(count == index) {
				if(count == 0) {
					returnVal = get(0);
					removeFirst();
					add(0, element);
				}
				else if(count == size) {
					returnVal = get(size);
					removeLast();
					add(size, element);
				}
				else {
					returnVal = get(index);
					remove(current);
					add(index, element);
				}
			}
		}
		return returnVal;
	}
}
