```java
@Test
public void test(){
   		when(someMock.getSerivce(argThat(pageRequestContainId(List.of(1,4,5,6))))).thenReturn(blablabla);
}

private ArgumentMatcher&lt;PageRequest&gt; pageRequestContainId(List&lt;Integer&gt; ids) {
