            pane.add(monkey, 0, 1);
            pane.add(elephant, 0, 2);
    
            Stream.of(cat, monkey, elephant).forEach(btn -&gt; btn.setMaxWidth(Double.POSITIVE_INFINITY));
    
            Scene scene = new Scene(pane, WIDTH, HEIGHT);
            stage.setScene(scene);
            stage.show();
        }
    }

