      Catch(Exception e){
         e.printStackTrace
      } 

with

     try{
        AdminUser adminUser= adminservice.CheckAdmin(user.getId);
     }
     Catch(Exception e){
        e.printStackTrace
        throw new RuntimeException(e);
     }

