**dataBaseConnection**

    package com.apporders;
    
    import java.time.Duration;
    import java.util.ArrayList;
    import java.util.HashMap;
    import java.util.Map;
    
    import org.bson.Document;
    
    import com.mongodb.MongoException;
    import com.mongodb.client.MongoClient;
    import com.mongodb.client.MongoClients;
    import com.mongodb.client.MongoDatabase;
    import com.mongodb.client.MongoCollection;
    import org.bson.types.ObjectId;
    
    import java.util.logging.Level;
    import java.util.logging.Logger;
    
    public class dataBaseConnection {
    
        private String url = &quot;&quot;;
    
        String getUrl() {
            return this.url;
        }
    
        public MongoClient establishDbConnection() {
    
            Logger.getLogger(&quot;org.mongodb.driver&quot;).setLevel(Level.WARNING);
            dataBaseConnection dbLink = new dataBaseConnection();
    
            try {
                MongoClient mongoClient = MongoClients.create(dbLink.getUrl());
                System.out.println(&quot;---------------------------------&quot;);
                System.out.println(&quot;Connected successfully to server.&quot;);
                System.out.println(&quot;---------------------------------&quot;);
    
                return mongoClient;
    
            } catch (MongoException e) {
                System.out.println(&quot;---------------------------------&quot;);
                System.err.println(&quot;An error occurred while attempting to connect: &quot; + e);
                System.out.println(&quot;---------------------------------&quot;);
                }
            
            return null;
    
        }
    
        public void insertData(String user, Duration sessionLength, MongoClient mongoClient, ArrayList&lt;String&gt; productId, ArrayList&lt;String&gt; deliveryAddress) {
    
            MongoDatabase database = mongoClient.getDatabase(&quot;gettingStarted&quot;);
            MongoCollection&lt;Document&gt; collection = database.getCollection(&quot;people&quot;);
            
    
            Map&lt;String, Object&gt; metaData = new HashMap&lt;&gt;();
            metaData.put(&quot;meta_one&quot;, &quot;xyz&quot;);
            metaData.put(&quot;meta_two&quot;, &quot;abc&quot;);
    
            collection.insertOne(new Document()
                            .append(&quot;_id&quot;, new ObjectId())
                            .append(&quot;user&quot;, user)
                            .append(&quot;session length (seconds)&quot;, sessionLength.toSeconds())
                            .append(&quot;meta_data&quot;, metaData)
                            .append(&quot;productId&quot;, productId)
                            .append(&quot;deliveryAddress&quot;, deliveryAddress)
            );
    
            mongoClient.close();
        }
    }

**App**

    public class App 
    {
    
        public static void main(String[] args) {
    
            Scanner scanner = new Scanner(System.in);
            boolean openSession = true;
            panel welcomePanel = new panel();
            user userName = new user();
    
            System.out.println(welcomePanel.showMetaData());
    
            welcomePanel.welcomeMsg();
            userName.setName(scanner.next());
    
            System.out.println(&quot;user logged: &quot; + userName.getName());
    
            if (userName.getName().equals(&quot;Root&quot;)) {
                rootPanel rootMsg = new rootPanel();
                rootMsg.welcomeMsg();
            }
    
            while(openSession) {
                Instant start = Instant.now();
                System.out.println(&quot;Session has been opened&quot;);
                System.out.println(&quot;1. Exit&quot;);
                System.out.println(&quot;2. Place item ID you are ordering&quot;);
                System.out.println(&quot;3. Place delivery addres&quot;);
    
                int userPick = scanner.nextInt(); 
    
                switch (userPick) {
                    case 1: openSession = false;
                    scanner.close();
                        
                    case 2: openSession = false;
                    ArrayList&lt;String&gt; productId = userName.userActivity();
    
                    case 3: openSession = false;
                    ArrayList&lt;String&gt; deliveryAddress = userName.userActivity();
                    userPick = scanner.nextInt();
    
    
                    Instant end = Instant.now();
                    Duration sessionLength = Duration.between(start, end);
                    System.out.println(&quot;Session time in seconds: &quot; + sessionLength.getSeconds());
                        
                    dataBaseConnection connection = new dataBaseConnection();
                        
                    try {
                        connection.insertData(
                            userName.getName(),
                            sessionLength,
                            connection.establishDbConnection(),
                            productId, //this thorws &quot;variable might not have been initialized&quot;, why?
                            deliveryAddress
                        );
    
                    }
    
                    catch (MongoTimeoutException e) {
                        System.err.println(&quot;Timeout error: &quot;+ e);
                        }
                } 
            }
        }
    }
