            File closedFile = localHandle.getFile().toFile();
            if (closedFile.exists() &amp;&amp; closedFile.isFile()) {
                logger.info(String.format(&quot;User %s closed file: \&quot;%s\&quot;&quot;, serverSession.getUsername(), localHandle.getFile().toAbsolutePath()));
