    public void incrementHoursBy(int hour) {
        // if statement which prevents negative hours from being inputted
        if (hour &lt; HOUR_MIN  ||  hour &gt; HOUR_MAX) {
            throw new IllegalArgumentException(&quot;Invalid input. Between 0 &amp; 23&quot;);
