            while(i &lt; n1) {
                arr[start + k] = leftSubArray[i];
                i++;
                k++;
            }
            while(j &lt; n2 &amp;&amp; (start + k) &lt; end) {
                arr[start + k] = rightSubArray[j];
                j++;
                k++;
            }
            
