    var mySqrt = function(x) {
    var ans = 1;
    if(x === 0){
        ans = 0;
    } else {
    for (let i = 1; i&lt; x;i++){
        if(i*i === x){
            ans = i;
            break;
        }
        if(i*i &gt;x){
            ans = i - 1;
            break;
        }
    }
    }
    return ans;
};

