    static int countValidData(String[] arrayDate) {
        
            //Initialize SimpleDateFormat
            SimpleDateFormat formatter = new SimpleDateFormat(&quot;dd/MM/yyyy&quot;);

            formatter.setLenient(false);

            int counter = 0;

            for (String currentWord : arrayDate) {
                
                //Iterate over each possible substring of currentWord
                //Our first substring will end 2 digits after the final &quot;/&quot; in the date
                for (int i = currentWord.lastIndexOf(&quot;/&quot;)+2; i &lt; currentWord.length() &amp;&amp; currentWord.lastIndexOf(&quot;/&quot;) != -1; i++){
                    
                    try {
                    
                        //Try to convert substring to Date using SimpleDateFormat
                        Date myDate = formatter.parse(currentWord.substring(0,i+1));
                    
                        //Increment counter by 1
                        counter += 1;
                        
                        //Stop looping the currentDate
                        i = currentWord.length();
                    
                    } catch (Exception e){
                        //If an exception was thrown, the date is invalid
                    } 
                }
            }

            return counter;
    }

