  for (char c : original.toCharArray()) {
    if (stack.isEmpty()) {
      stack.push(c);
    }
    else {
      final char t = stack.peek();
      boolean matches = false;
      for(Iterator&lt;String&gt; iterator = remove.iterator(); iterator.hasNext() &amp;&amp; !matches; ) {
