        public void setup() {
            driver.manage().timeouts().implicitlyWait(Duration.ofSeconds(20));
            driver.manage().window().maximize();
            driver.get(&quot;mysite&quot;);
        }

    }

    public class MyPageTest extends BaseSeleniumTest {

        @BeforeEach
        public void setup(){
            super.setup();
        }
        
    }

