public int nthUglyNumber(int n) {
    if (n &lt; 7) return n;
    PriorityQueue&lt;Long&gt; heap = new PriorityQueue&lt;&gt;();
    Set&lt;Long&gt; set = new HashSet&lt;&gt;();
    heap.add(1L);
