    private void insert(double price){
        for(int i = 0; i &lt; keys.size(); i++){
            if(price &gt; keys.get(i)){
                keys.add(null);
                for(int j = keys.size()-1; j &gt; i; j--){
                    Collections.swap(keys, j, j-1);
                }
                keys.add(price);
                Collections.swap(keys, keys.size()-1, i);
                keys.remove(keys.size()-1);
                return;
            }
        }
        keys.add(price);
    }

