    inline fun &lt;T&gt; List&lt;T&gt;.moveItemToFirstPosition(predicate: (T) -&gt; Boolean): List&lt;T&gt; {
        for (element in this.withIndex()) {
            if (predicate(element.value)) {
                return this.toMutableList().apply {
                    removeAt(element.index)
                    add(0, element.value)
                }.toList()
            }
        }
