public static String insertDashBetweenOddNumbers(long num) {
    String str = String.valueOf(num);
    StringBuilder buf = new StringBuilder();

    for (int i = 0; i &lt; str.length(); i++) {
        int cur = str.charAt(i) - &#39;0&#39;;

        if (i &gt; 0) {
            int prv = str.charAt(i - 1) - &#39;0&#39;;

            if (isOdd(prv) &amp;&amp; isOdd(cur))
                buf.append(&#39;-&#39;);
        }

        buf.append(cur);
    }

    return buf.toString();
}

private static boolean isOdd(int num) {
    return (num &amp; 1) == 1;
}
