        i = 0;
        char[] newCharacters = new char[rows * numberOfColumnsInMatrix];
        for (col = 0; col &lt; numberOfColumnsInMatrix; col++) {
            for (row = 0; row &lt; rows; row++) {
                newCharacters[i++] = matrix[row][col];
            }
        }
        return new String(newCharacters);
