    function divisors(num, uniqueDict) {
        var count = 0;
        var x = 1;
        while (x * x &lt;= num) {
            if (parseInt(num/x) === num/x) { // is divisor
                if (uniqueDict[num/x] &gt; 0) {
                    count += uniqueDict[num/x];
