public class NumEqualUtils {
    static boolean areEqualsNumerical(Serializable... values) {
      return areNumerical(values) &amp;&amp; 
        Stream.of(values)
          .map(s -&gt; NumberUtils.createBigDecimal(s.toString()).doubleValue() )
          .distinct()
          .count() == 1;
