		String absPath = diskFileItem.getStoreLocation().getAbsolutePath();
		File file = new File(absPath);

		// trick to implicitly save on disk small files (&lt;10240 bytes by default)
		if (!file.exists()) {
			try {
				file.createNewFile();
				multipartFile.transferTo(file);
			} catch (IOException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}

		}

		return file;
	}
