    private Node balanceTree(LinkedList&lt;Node&gt; tree, int first, int last) {
        if (first &gt; last) {
            return null;
        }
        int temp = first + last;
        int mid = temp / 2;
        if (temp % 2 == 1) {
            mid++;
        }
        Node midNode = tree.get(mid);
        midNode.left = balanceTree(tree, first, mid - 1);
        midNode.right = balanceTree(tree, mid + 1, last);
