Starting with Unity 2018 you should implement the `IPostGenerateGradleAndroidProject` [interface](https://docs.unity3d.com/ScriptReference/Android.IPostGenerateGradleAndroidProject.html) which will allow you to edit the android manifest after Unity produces it. In the code below I have added methods to set the microphone permission, set hardware acceleration, and set your application theme ( replace `SetMicrophonePermission()` to your preferred method, which is called when Unity calls `OnPostGenerateGradleAndroidProject()`).

Add the following code to `Assets/Editor/ModifyUnityAndroidAppManifestSample.cs`

    using System.IO;
    using System.Text;
    using System.Xml;
    using UnityEditor.Android;
    
    public class ModifyUnityAndroidAppManifestSample : IPostGenerateGradleAndroidProject
    {
    
        public void OnPostGenerateGradleAndroidProject(string basePath)
        {
            // If needed, add condition checks on whether you need to run the modification routine.
            // For example, specific configuration/app options enabled
    
            var androidManifest = new AndroidManifest(GetManifestPath(basePath));
    
            androidManifest.SetMicrophonePermission();
    
            // Add your XML manipulation routines
    
            androidManifest.Save();
        }
    
        public int callbackOrder { get { return 1; } }
    
        private string _manifestFilePath;
    
        private string GetManifestPath(string basePath)
        {
            if (string.IsNullOrEmpty(_manifestFilePath))
            {
                var pathBuilder = new StringBuilder(basePath);
                pathBuilder.Append(Path.DirectorySeparatorChar).Append(&quot;src&quot;);
                pathBuilder.Append(Path.DirectorySeparatorChar).Append(&quot;main&quot;);
                pathBuilder.Append(Path.DirectorySeparatorChar).Append(&quot;AndroidManifest.xml&quot;);
                _manifestFilePath = pathBuilder.ToString();
            }
            return _manifestFilePath;
        }
    }
    
    
    internal class AndroidXmlDocument : XmlDocument
    {
        private string m_Path;
        protected XmlNamespaceManager nsMgr;
        public readonly string AndroidXmlNamespace = &quot;http://schemas.android.com/apk/res/android&quot;;
        public AndroidXmlDocument(string path)
        {
            m_Path = path;
            using (var reader = new XmlTextReader(m_Path))
            {
                reader.Read();
                Load(reader);
            }
            nsMgr = new XmlNamespaceManager(NameTable);
            nsMgr.AddNamespace(&quot;android&quot;, AndroidXmlNamespace);
        }
    
        public string Save()
        {
            return SaveAs(m_Path);
        }
    
        public string SaveAs(string path)
        {
            using (var writer = new XmlTextWriter(path, new UTF8Encoding(false)))
            {
                writer.Formatting = Formatting.Indented;
                Save(writer);
            }
            return path;
        }
    }
    
    
    internal class AndroidManifest : AndroidXmlDocument
    {
        private readonly XmlElement ApplicationElement;
    
        public AndroidManifest(string path) : base(path)
        {
            ApplicationElement = SelectSingleNode(&quot;/manifest/application&quot;) as XmlElement;
        }
    
        private XmlAttribute CreateAndroidAttribute(string key, string value)
        {
            XmlAttribute attr = CreateAttribute(&quot;android&quot;, key, AndroidXmlNamespace);
            attr.Value = value;
            return attr;
        }
    
        internal XmlNode GetActivityWithLaunchIntent()
        {
            return SelectSingleNode(&quot;/manifest/application/activity[intent-filter/action/@android:name=&#39;android.intent.action.MAIN&#39; and &quot; +
                    &quot;intent-filter/category/@android:name=&#39;android.intent.category.LAUNCHER&#39;]&quot;, nsMgr);
        }
    
        internal void SetApplicationTheme(string appTheme)
        {
            ApplicationElement.Attributes.Append(CreateAndroidAttribute(&quot;theme&quot;, appTheme));
        }
    
        internal void SetStartingActivityName(string activityName)
        {
            GetActivityWithLaunchIntent().Attributes.Append(CreateAndroidAttribute(&quot;name&quot;, activityName));
        }
     

        internal void SetHardwareAcceleration()
        {
            GetActivityWithLaunchIntent().Attributes.Append(CreateAndroidAttribute(&quot;hardwareAccelerated&quot;, &quot;true&quot;));
        }
    
        internal void SetMicrophonePermission()
        {
            var manifest = SelectSingleNode(&quot;/manifest&quot;);
            XmlElement child = CreateElement(&quot;uses-permission&quot;);
            manifest.AppendChild(child);
            XmlAttribute newAttribute = CreateAndroidAttribute(&quot;name&quot;, &quot;android.permission.RECORD_AUDIO&quot;);
            child.Attributes.Append(newAttribute);
        }
    }

