      public static List&lt;Element&gt; getElements(final NodeList nodeList) {
        final int len = nodeList.getLength();
        final List&lt;Element&gt; elements = new ArrayList&lt;&gt;(len);
        for (int i = 0; i &lt; len; i++) {
