    		for (String s : arr) {
    			try {
    				double n = Double.parseDouble(s);
    				System.out.println(s + &quot; =&gt; &quot; + n);
    			} catch (NumberFormatException | NullPointerException e) {
    				System.out.println(s + &quot; is not a valid number.&quot;);
