public static int hoarePartition(int[] arr, int low, int high,int pivot)
    {
        int i = low, j = high;

        while (true) {
            // Find leftmost element greater
            // than or equal to pivot

            while (i&lt;j &amp;&amp; arr[i] &lt; pivot){
                i++;
            }

            // Find rightmost element smaller
            // than or equal to pivot
            while (j&gt;i &amp;&amp; arr[j] &gt; pivot){
                j--;
            }
            // If two pointers met.
            if (i &gt;= j)
                return j;
            int temp = arr[i];
            arr[i] = arr[j];
            arr[j] = temp;
            // swap(arr[i], arr[j]);
