            JXTabbedPane tabbedPane = new JXTabbedPane(JTabbedPane.LEFT);
            AbstractTabRenderer renderer = (AbstractTabRenderer)tabbedPane.getTabRenderer();
            renderer.setPrototypeText(&quot;This text is a prototype&quot;);
