            public JXTabbedPane(int tabPlacement, int tabLayoutPolicy) {
                super(tabPlacement, tabLayoutPolicy);
            }
    
            public ITabRenderer getTabRenderer() {
                return tabRenderer;
            }
    
            public void setTabRenderer(ITabRenderer tabRenderer) {
