        return new LoggingMeterRegistry(new LoggingRegistryConfig() {
            @Override
            public Duration step() {
                return Duration.ofSeconds(10); // log every 10 seconds
            }

            @Override
            public String get(String key) {
                return null;
            }
        }, Clock.SYSTEM);
    }


