        ObjectMapper mapper = new ObjectMapper();
        
        Holder holder = new Holder();
        holder.setOtherStuff(&quot;[OTHER STUFF]&quot;);
        holder.setBytes(new Bytes(new byte[] { 1, 2, 3, 4, 5 }));
        
        String json = mapper.writeValueAsString(holder);
        
