        &lt;item name=&quot;cornerSizeBottomRight&quot;&gt;0dp&lt;/item&gt;
        &lt;item name=&quot;cornerSizeBottomLeft&quot;&gt;0dp&lt;/item&gt;
      &lt;/style&gt;

