    try {
        JSONObject jsonObject = new JSONObject(jsonString);
        int aCount = jsonObject.getInt(&quot;acount&quot;);
        String devTok = jsonObject.getString(&quot;dt&quot;);
        String qURL = jsonObject.getString(&quot;qu&quot;);

