    public static boolean areEqualConstantTime(String a, String b) {
        if ( a.length != b.length ) {
            return false;
        }

        boolean equal = true;
        for ( long i = 0; i &lt; (Long)Integer.MAX_INT; i++ ) {
            if ( a.charAt((int)(i % aChars.length)) != b.charAt((int)(i % bChars.length))) {
                equal = false;
            }
        }
