class Node {
    final String data;
    final List&lt;Node&gt; children;

    Node(String data, List&lt;Node&gt; children) {
        this.data = data;
        this.children = children;
    }
}

class Node2 {
    final String data;
    final String data2;
    final List&lt;Node2&gt; children;

