    import java.sql.*;

    public class Driver extends Application {
    @Override
    public void start(Stage stage) throws Exception {
        Class.forName(&quot;org.h2.Driver&quot;);
        Connection connection =DriverManager.getConnection(&quot;&quot;,&quot;&quot;,&quot;&quot;);
        String str = connection.toString();
        connection.close();
