import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.nio.file.Files;
import java.text.SimpleDateFormat;

public class MyFileWriter {
	private String fileName;
	private String backupFilePath;
	private FileWriter fw;
	
	public MyFileWriter(String file, String backupFilePath) throws IOException{
        fw = new FileWriter(file);
        this.fileName = file;
        this.backupFilePath = backupFilePath;
    }

    public MyFileWriter(File file, boolean append, String backupFilePath) throws IOException{
        this.fw = new FileWriter(file, append);
        this.fileName = file.getName();
        this.backupFilePath = backupFilePath;
    }

	public void write(String str, int off, int len) throws IOException {
        successBackupFile(this.fileName,this.backupFilePath);
        try {
        this.fw.write(str, off, len);}
        catch(IOException e) {
        	System.out.println(e);
        }
        finally {
        	fw.close();
        }

    }

    private static void successBackupFile(String fileToBackup,String backupFilePath) {
    	
    	String timeStamp = fileToBackup+&quot;_&quot;+ new SimpleDateFormat(&quot;yyyy.MM.dd.HH.mm.ss&quot;).format(new java.util.Date());

        File f = new File(backupFilePath+&quot;\\&quot;+ timeStamp);
        try
        {
            Files.copy(new File(fileToBackup).toPath(), f.toPath());
        }
        catch(IOException ioe){
           System.out.println(ioe);
        }

	}
}
```

