public class Link implements IEventHandler  {
    private final LayoutInfo info;
    private final PDFReportGenerator generator;

    public Link(LayoutInfo info, PDFReportGenerator generator) {
        this.info = info;
        this.generator = generator;
    }

    @Override
    public void handleEvent(Event event) {
        HashMap&lt;Integer, ArrayList&lt;LinkDetails&gt;&gt; links = info.getLinks();

        PdfPage page = ((PdfDocumentEvent) event).getPage();
        int pageNumber = page.getDocument().getPageNumber(page);

        if (links.containsKey(pageNumber)) {
            ArrayList&lt;LinkDetails&gt; linkList = links.get(pageNumber);
