      public static int choose(int n, int k) {
        if (k &lt; 0 || k &gt; n) {
          return 0;
        }
        if (k &gt; n / 2) {
          k = n - k;
        }
    
        long denominator = 1;
        long numerator = 1;
        for (int i = 1; i &lt;= k; i++) {
          denominator *= i;
          numerator *= (n + 1 - i);
        }
        return (int) (numerator / denominator);
