   private String getFolderValue(TableRow row) {
        String cote = row.getCellValue(&quot;B&quot;);
        String typologie = row.getCellValue(&quot;G&quot;);
        String description = row.getCellValue(&quot;Q&quot;);

        return Stream.of(
