            return &quot;Catalogues&quot;;
        }
        return null;
    }

    private String parseFromCotePU(String cote, String typologie, String description) {
        if (cote.startsWith(&quot;PU&quot;)) {
            final var topologyContains = Stream.of(&quot;flyer&quot;, &quot;presse&quot;, &quot;affiche&quot;).anyMatch(typologie::contains);
