                    &quot;field2Bar&quot;: [{$match: {&quot;field2&quot;: {$eq: &quot;bar&quot;}}}, {$count: &quot;count&quot;}],
                    &quot;field3Raw&quot;: [{$match: {&quot;field3&quot;: {$eq: &quot;raw&quot;}}}, {$count: &quot;count&quot;}],
                }
            },
            {
                $project: {
                    &quot;count1&quot;: {$arrayElemAt: [&quot;$field1Foo.count&quot;, 0]},
                    &quot;count2&quot;: {$arrayElemAt: [&quot;$field2Bar.count&quot;, 0]},
