```
public class KSCustomizer implements KafkaStreamsCustomizer
{
    private AppConfig config;

    AppHealthIndicator healthIndicator;

    HealthEndpoint healthEndpoint;

    static ConfigurableApplicationContext context;


    public KSCustomizer(AppConfig config, 
            ConfigurableApplicationContext context, AppHealthIndicator healthIndicator)
    {
        this.config = config;
        KSCustomizer.context = context
        this.healthIndicator = healthIndicator;
    }

    @Override
    public void customize(KafkaStreams kafkaStreams)
    {

        kafkaStreams.setStateListener((newState, oldState) -&gt; {
            // update app-state metrics triggered by state change
            metrics.update(processMethod, Application.STATE_CHANGES, 1);
            healthIndicator.setCurrent(newState);

            if (newState == KafkaStreams.State.PENDING_SHUTDOWN)
            {
                log.info(&quot;Kafka is shutting down&quot;);
            }

            if (newState == KafkaStreams.State.ERROR)
            {
                log.warn(&quot;KStream entered into Error state.&quot;);
                if (config.getRestart().isEnabled())
                {
                    long delay = config.getRestart().getDelay();
                    // calculate delay
                    // long delay = (long) (attempted * (Math.pow(multiplier, attempted - 1))) *
                    // factor;
                    try
                    {
                        log.warn(&quot;Error State; about to restart application in {} seconds&quot;, delay);

                        Thread.sleep(delay * 1000);
                        restart();
                    }
                    catch (InterruptedException e)
                    {
                        log.error(&quot;Interrupted Exception&quot;, e);
                    }
                }
                else
                {
                    context.close();
                    kafkaStreams.cleanUp();
                }
            }

            if (oldState == KafkaStreams.State.RUNNING)
            {
                log.warn(&quot;Kafka stream process transitioned from {} to {}&quot;, oldState, newState);
            }
        });

    }

    public static void restart() throws InterruptedException {
        log.info(&quot;About to restart Application ...&quot;);
        ApplicationArguments args = context.getBean(ApplicationArguments.class);
        Thread thread = new Thread(() -&gt; {
            try
            {
                context.close();
                context = SpringApplication.run(Application.class, args.getSourceArgs());
            }
            catch (Exception e)
            {
                log.error(&quot;Failure in Restarting application: &quot;, e);
            }
        });

        thread.setDaemon(false);
        thread.start();
        log.info(&quot;Restarted Application ...&quot;);
    }
}
