            while (count.get() == capacity) {

                notFull.await();

            }

            enqueue(node);

            c = count.getAndIncrement();

            if (c + 1 &lt; capacity)

                notFull.signal();

        } finally {

            putLock.unlock();

        }

        if (c == 0)

