    public static &lt;T&gt; void shuffleList(List&lt;T&gt; a) {
        int n = a.size();
        for (int i = 0; i &lt; n; i++) {
          int change = i + Random.nextInt(n - i);
          swap(a, i, change);
        }
      }
    
      private static &lt;T&gt; void swap(List&lt;T&gt; a, int i, int change) {
        T helper = a.get(i);
        a.set(i, a.get(change));
        a.set(change, helper);
      }

