                CommPort commPort = portIdentifier.open(this.getClass().getName(), PORT_RATE);
                if (commPort instanceof SerialPort) {
                    serialPort = (SerialPort) commPort;
    
                    serialPort.setSerialPortParams(PORT_RATE,SerialPort.DATABITS_8,SerialPort.STOPBITS_1,SerialPort.PARITY_NONE);
    
                    serialPort.addEventListener(this);
                    serialPort.notifyOnDataAvailable(true);
                    return true;
                } else
                    return false;
            }
        }
    
