    ZipOutputStream zout = new ZipOutputStream(fout);
    for all files
    {
        ZipEntry ze = new ZipEntry(filename);
        zout.putNextEntry(ze);
        send data to zout;
        zout.closeEntry();
    }
    zout.close();

